import {useEffect, useState} from 'react';

import {getAllRedWines} from './ApiService';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Image from 'react-bootstrap/Image';

import logo from './logo.svg';
import './App.css';

function App() {
  const [redWines, setRedWines] = useState([]);

  useEffect(() => {
    const fetchRedWines = async () => {
      const wines = await getAllRedWines();
      setRedWines(wines);
    }

    fetchRedWines();
  }, []);

  return (
    <Container className='App'>
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <Row>
          {redWines.map((wine) => {
            return (
              <Col className='border border-danger'key={wine.id}>
                {console.log(wine)}
                <Image src={wine.image} fluid rounded>

                </Image>
                <div className=''>
                  <p>{wine.wine}</p>
                </div>
              </Col>
            )
          })}
        </Row>
      </header>
    </Container>
  );
}

export default App;
