import {useEffect, useState} from 'react';

import {getAllRedWines} from './ApiService';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Card from 'react-bootstrap/Card';

import logo from './logo.svg';
import './App.css';

function App() {
  const [redWines, setRedWines] = useState([]);

  useEffect(() => {
    const fetchRedWines = async () => {
      const wines = await getAllRedWines();
      setRedWines(wines);
    }

    fetchRedWines();
  }, []);

  return (
    <Container className='App'>
        <img src={logo} className="App-logo" alt="logo" />
        <Row xs={1} md={3}>
          {redWines.map((wine) => {
            return (
              <Col className='border border-danger'key={wine.id}>
                {console.log(wine)}
                <Card>
                  <Card.Img variant='top' height='200px' width='' src={wine.image} />
                  <Card.Body>
                    <Card.Title>{wine.wine}</Card.Title>
                    <Card.Text>{wine.location}</Card.Text>
                  </Card.Body>
                </Card>
              </Col>
            )
          })}
        </Row>
    </Container>
  );
}

export default App;
